            </span>
        </div>
    </form>
</div>
</div>
<br/>
               <h2>Nearby restaurants</h2>
               <div>
               {listOfRestaurants}
               </div>
               <br/>
               <div className="pagination">
               <Pagination itemsCount={count} pageSize={pageSize} 
                        currentPage = {this.state.currentPage}
                        onPageChange={this.handlePageChange} /> 
                        </div>
           </div>

        )
    }
}

export default Home;
