class Home extends Component {
    //call the constructor method
    constructor(props) {
        // Call the constructor of Super class i.e The Component
        super(props);
        this.dishesList = '1. Paella 2. Calamari (Fried Squid) 3. Chicken Tikka Masala 4. Escargots 5. Frog Legs 6. Yorkshire Pudding 7. Burrito 8. Fried Green Tomatoes 9. Tortilla (Spanish) 10. Stinking Bishop 11. Goulash 12. Pizza 13. Pasta Fagioli 14. Chorizo Riojano 15. Jamon Iberico De Bellota 16. Eggs Benedict 17. Egg Fried Rice 18. Spring Roll 19. Daar Chawal 20. Baclava 21. Quiche Lorraine 22. Oxtail Soup 23. Alligator 24. Alligator Jerky 25. Kangaroo 26. Ostrich 27. Guinea Fowl 28. Guinea Pig 29. Ratatouille 30. Thai Green Curry 31. Bratwurst 32. Twinkies 33. Cream Tea 34. Cucumber Sandwiches 35. Sugarcane 36. Gyros 37. Souvlaki 38. Bocadillo De Calamares 39. Octopus 40. Anchovies 41. Olives 42. Aged Balsamic Vinegar 43. Strawberries and Cream 44. Feta Cheese 45. Couscous 46. Thai Iced Tea 47. Chinese Five Spice 48. Chicken and Waffles 49. French Onion Soup 50. English Mustard 51. English Breakfast 52. Pumpkin Pie 53. Polvorones (Mexican Wedding Cookies) 54. Polenta 55. Sichuan Hot Pot 56. Lancashire Hot Pot 57. Scouse 58. Three Musketeers 59. Indian Fry Bread 60. Key Lime Pie 61. Kraut 62. Churros 63. Spicy Tuna Sushi 64. Awabi (あわび) 65. Arugula 66. Ataulfo Mango 67. Aunt Bessie\'s 68. Aioli 69. Apple Jacks 70. Asian Pear 71. African Cucumber 72. Agedashi Tōfu 73. Ahmad Tea 74. Ajowan 75. Akbar Mashti(Saffron Ice Cream) 76. Alfalfa Sprouts 77. Azuki Bean 78. Actimel 79. Avocado Paste 80. Absinthe 81. Aniseed 82. Reese\'s Peanut Butter Cups 83. Rye Whiskey 84. Rugelach 85. Ricotta 86. Root Beer Float 87. Nesquik RD 88. Rhubarb Crumble 89. Texas Roadhouse 90. Trifle 91. Twizzlers 92. Turkey Jerky 93. Tiramisu 94. Toblerone 95. Tapioca 96. Taco Bell 97. Tabasco 98. Tsukemono (漬け物) 99. Sweet Potato Pie 100. Squirrel 101. Squid 102. Star Fruit 103. Stella Artois 104. Stilton 105. Stuffed Peppers With Feta 106. Stollen 107. Sushi 108. Sydney Rock Oyster 109. Souffle 110. Soda Bread 111. Cranberry Sauce 112. Falafel Sandwich 113. Grilled Cheese Sandwich 114. Hijiki 115. Liver 116. Lobster Roll 117. Haggis 118. Huevos Rancheros 119. Hummus 120. Patatas Bravas 121. Spam 122. Spanish Rice 123. Bird\'s Nest Soup 124. Foster\'s 125. San Miguel Pale Pilsen 126. Shrimp and Grits 127. Olive Oil 128. Chicken Pot Pie 129. Rye Bread 130. Braised Lamb Cheeks 131. Licorice Ice Cream 132. Shirako 133. Greek Yogurt 134. Greek Salad 135. Bruschetta 136. Corndogs 137. Pad Kee Mao 138. Khao Pad (Fried Rice) 139. Korokke, Croquette 140. Gorgonzola 141. French Fries 142. Deviled Ham 143. Vita-Wheat 144. Whelks 145. Frappuccino 146. Tapenade 147. Soy Sauce 148. Chow Mein 149. Krispy Kreme Glazed Donut 150. Pretzels';
        this.noNumList = this.dishesList.replace(/\d+/g, '');
        // maintain the state required for this component
        this.state = {
            profileInfo: [],
            selectedFile: '',
            selectedFile: '',
            images: '',
            foodName: '',
            nextPage: false,
            foodPic : null,
            recipe: false,
            lat: 0,
            long: 0
        };
        this.handleSubmit = this.handleSubmit.bind(this);
        this.uploadFile = this.uploadFile.bind(this);
        this.findRestaurants = this.findRestaurants.bind(this);
        this.findRecipe = this.findRecipe.bind(this);
        this.findRecipeSearch = this.findRecipeSearch.bind(this);
        this.searchChangeHandler = this.searchChangeHandler.bind(this);
        this.findLocation = this.findLocation.bind(this);
        // Bind the handlers to this class
    }

    uploadFile = (e) => {
        e.preventDefault();
        document.getElementById('getFile').click();
    }
    async findRestaurants () {
        await this.findLocation();
        this.setState({
        nextPage: true
       });
    }
    async findLocation(){
        if("geolocation" in navigator){
            navigator.geolocation.getCurrentPosition((position)=>{
                console.log(position.coords.latitude);
                console.log(position.coords.longitude);
                localStorage.setItem('latitude', position.coords.latitude);
                localStorage.setItem('longitude', position.coords.longitude);
            });
        }
        else {
            alert("Denied Permission");
        }
    }
    async findRecipe () {
        this.setState({
            recipe: true
        });
    }
